import React from 'react';
import { useState } from 'react';
import './App.css';
import img from './Images/R.jpeg';

function App() {
  const [inputs, setInputs] = useState({});
  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    setInputs(values => ({...values, [name]: value}))
  }

  const handleSubmit = (event) => {
    event.preventDefault();
    alert(inputs);
  }
  return (
    <div class="main">
      <img src= {img} alt = ""/>
      <form onSubmit={handleSubmit}>
        <div className="form-element">
          <input type="text" id="name" name="fname" placeholder="Enter Full Name" value={inputs.name || ""} onChange={handleChange} />
        </div><br></br><br></br>
        <div class="form-element">
          <input type="text" id="email" name="email" placeholder="Enter Email ID" value={inputs.email || ""} onChange={handleChange} />
        </div><br></br><br></br>
        <div class="form-element">
          <input type="text" id="phone" name="phone" placeholder="Enter Phone Number" value={inputs.phone || ""} onChange={handleChange} />
        </div><br></br><br></br>
        <div class="form-element">
          <input type="password" id="pwd2332" name="pwd332" placeholder="Enter Password" required="" value={inputs.password || ""} />
        </div><br></br><br></br>
        <div class="form-element inputValue">
          <input type="password" id="pwd" name="pwd" autocomplete="current-password" required="" placeholder="Enter Confirm Password" value={inputs.password || ""}/>
            <span><i class="far fa-eye" id="togglePassword"></i></span>
        </div><br></br><br></br>
        <div class="form-element">
            <button onClick="add()">Submit</button>
        </div>
      </form>
    </div>
  );
}

export default App;
