import React from 'react';
import './App.css';
import img from './Images/R.jpeg';

class NameForm extends React.Component{
  constructor(props){
    super(props)
    this.state = {name:'',email:''}
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange(event){
    console.log(event.target.value)
    let obj = {};
    obj[event.target.name] = event.target.value
    this.setState(obj)
  }
  handleSubmit(event){
    alert(this.state.value,this.state.email)
    event.preventDefault()
  }
  render(){
    return (
      <div className="main">
        <img src= {img} alt = ""/>
        <form >
          <div className="form-element">
            <input type="text" id="name" name="fname" placeholder="Enter Full Name" value={this.state.value} onChange={this.handleChange}/>
          </div><br></br><br></br>
          <div className="form-element">
            <input type="text" id="email" name="email" value={this.state.email} onChange={this.handleChange}  placeholder="Enter Email ID"/>
          </div><br></br><br></br>
          <div className="form-element">
            <input type="text" id="phone" name="phone" placeholder="Enter Phone Number" value={this.state.phone} onChange={this.handleChange}/>
          </div><br></br><br></br>
          <div className="form-element">
            <input type="password" id="pwd2332" name="pwd332" placeholder="Enter Password" required="" value={this.state.password} onChange={this.handleChange}/>
          </div><br></br><br></br>
          <div className="form-element inputValue">
            <input type="password" id="pwd" name="pwd" autoComplete="current-password" required="" placeholder="Enter Confirm Password" value={this.state.password} onChange={this.handleChange}/>
              <span><i className="far fa-eye" id="togglePassword"></i></span>
          </div><br></br><br></br>
          <div className="form-element">
              <button onClick={this.handleSubmit}>Submit</button>
          </div>
        </form>
      </div>
    );
  }
}

export default NameForm;
